mapper.insert:
insert into signature (username, signature, privatekey, 
  publickey, creat_time, update_time
  )
values (#{username,jdbcType=VARCHAR}, #{signature,jdbcType=VARCHAR}, #{privatekey,jdbcType=VARCHAR}, 
  #{publickey,jdbcType=VARCHAR}, now(), now()
  )

mapper.select:
select count(1)
from signature
where username=#{username}

mapper.update:
update signature
set signature=#{sign},update_time=now()
where username=#{username}

mapper.select:
select signature
from signature
where username=#{username}

select publickey
from signature
where username=#{username}
